// tinymce.ScriptLoader.load(tinyMCE.baseURL + '/plugins/droplets/droplets.php');

tinymce.PluginManager.add('about', function(editor, url) {

    // Adds a menu item to the tools menu
    editor.addMenuItem('about', {
        text: 'About',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'About',
                url: url+'/about.php',//'http://www.tinymce.com', // here the dialog
                width: 400,
                height: 400,
                inline: false,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });

});